    private void jbCE_salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCE_salvarActionPerformed
        if (validarCampos()) {
            validaBotoes(true);
            ativacampos(false);
            controllerConexao = new ControllerConexao();
            controllerConexao.manutencaoConexao("inserir", jTextFieldNomeBanco.getText(), jTextFieldPassword.getText(), jTextFieldDriver.getText(), jTextFieldDrivermanager.getText(), jTextFieldBaseDados.getText(), jTextFieldMsg.getText());
            //dispose();
            //new Login().setVisible(true);           
        }
    }//GEN-LAST:event_jbCE_salvarActionPerformed

    private void jBEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBEditActionPerformed
        validaBotoes(false);
        ativacampos(true);
    }//GEN-LAST:event_jBEditActionPerformed

