 public void validaBotoes(Boolean v) {
        jBEdit.setEnabled(v);
        jbCE_cancelar.setEnabled(!v);
        jbCE_salvar.setEnabled(!v);
    }

    public void ativacampos(boolean v) {
        jTextFieldBaseDados.setEditable(v);
        jTextFieldDriver.setEditable(v);
        jTextFieldDrivermanager.setEditable(v);
        jTextFieldMsg.setEditable(v);
        jTextFieldNomeBanco.setEditable(v);
        jTextFieldPassword.setEditable(v);
    }

    private boolean validarCampos() {
        if (jTextFieldNomeBanco.getText().equals("")) {
            jTextFieldNomeBanco.requestFocus();
            return false;
        }
        if (jTextFieldPassword.getText().equals("")) {
            jTextFieldPassword.requestFocus();
            return false;
        }
        if (jTextFieldDriver.getText().equals("")) {
            jTextFieldDriver.requestFocus();
            return false;
        }
        if (jTextFieldDrivermanager.getText().equals("")) {
            jTextFieldDrivermanager.requestFocus();
            return false;
        }
        if (jTextFieldBaseDados.getText().equals("")) {
            jTextFieldBaseDados.requestFocus();
            return false;
        }
        if (jTextFieldMsg.getText().equals("")) {
            jTextFieldMsg.requestFocus();
            return false;
        }

        return true;
    }

